package thread.cas.increment;import java.util.concurrent.locks.Lock;import java.util.concurrent.locks.ReentrantLock;public class ReentrantLockInteger implements IncrementInteger{    private final Lock lock = new ReentrantLock();    private int value;    @Override    public void increment() {        lock.lock();        try {            value++;        } finally {            lock.unlock();        }    }    @Override    public int get() {        lock.lock();        try {            return value;        } finally {            lock.unlock();        }    }}