package thread.start.test;import thread.util.MyLogger;public class StartTest4Main {    public static void main(String[] args) {        new Thread(new Printer("A", 1000), "Thread-A").start();        new Thread(new Printer("B", 500), "Thread-B").start();    }    static class Printer implements Runnable {        private final String threadName;        private final int countMs;        public Printer(String threadName, int countMs) {            this.threadName = threadName;            this.countMs = countMs;        }        @Override        public void run() {            for (; ; ) {                MyLogger.log(threadName);                try {                    Thread.sleep(countMs);                } catch (InterruptedException e) {                    throw new RuntimeException(e);                }            }        }    }}