package ref;import java.util.Scanner;public class ProductOrderMain2 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("입력할 주문의 개수를 입력하세요: ");        int n = scanner.nextInt();        scanner.nextLine();        ProductOrder[] orders = new ProductOrder[n];        for (int i = 0; i < orders.length; i++) {            System.out.println((i + 1) + "번째 주문 정보를 입력하세요.");            System.out.print("상품명을 입력하세요: ");            String productName = scanner.nextLine();            System.out.print("가격을 입력하세요: ");            int price = scanner.nextInt();            System.out.print("수량을 입력하세요: ");            int quantity = scanner.nextInt();            scanner.nextLine();            orders[i] = createOrder(productName, price, quantity);        }        printOrder(orders);        System.out.println("총 결제 금액: " + getTotalAmount(orders));    }    static ProductOrder createOrder(String productName, int price, int quantity) {        ProductOrder order = new ProductOrder();        order.productName = productName;        order.price = price;        order.quantity = quantity;        return order;    }    static void printOrder(ProductOrder[] productOrders) {        for (ProductOrder productOrder : productOrders) {            System.out.println(                "상품명: " + productOrder.productName + ", 가격: " + productOrder.price + ", 수량: " + productOrder.quantity);        }    }    static int getTotalAmount(ProductOrder[] productOrders) {        int totalAmount = 0;        for (ProductOrder productOrder : productOrders) {            totalAmount += productOrder.price * productOrder.quantity;        }        return totalAmount;    }}