package com.brothergeol;public class SampleServiceProxy implements SampleService {    SampleService sampleService;    public SampleServiceProxy(SampleService sampleService) {        this.sampleService = sampleService;    }    @Override    public void test(Sample sample) {        System.out.println("proxy 객체에서 변경하고 싶은 사항들을 여기서 처리할 수 있습니다.");        sampleService.test(sample);    }}