package com.brothergeol;import java.lang.reflect.InvocationHandler;import java.lang.reflect.Method;import java.lang.reflect.Proxy;import org.junit.jupiter.api.Test;public class JavaDynamicProxyTest {    SampleService sampleService = (SampleService) Proxy.newProxyInstance(SampleService.class.getClassLoader(), new Class[]{SampleService.class},        new InvocationHandler() {            SampleService sampleService = new DefaultSampleService();            @Override            public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {                if (method.getName().equals("test")) {                    System.out.println("proxy 객체에서 변경하고 싶은 사항들을 여기서 처리할 수 있습니다.");                    Object invoke = method.invoke(sampleService, args);                    System.out.println("proxy 객체에서 변경하고 싶은 사항들을 여기서 처리할 수 있습니다.");                    return invoke;                }                return method.invoke(sampleService, args); }        });    @Test    void proxy_test() {        Sample sample = new Sample();        sample.setName("java dynamic proxy");        sampleService.test(sample);    }}