package com.brothergeol;// 기본적으로 annotation 은 주석(comment) 와 같다.@MyAnnotationpublic class Sample {    private String one = "one";    private static String two = "two";    private static final String three = "three";    public String four = "four";    // @MyAnnotation //@Target({ElementType.METHOD, ElementType.CONSTRUCTOR})    protected String five = "five";    public Sample() { }    public Sample(String one, String four, String five) {        this.one = one;        this.four = four;        this.five = five;    }    @MyAnnotation    private void six() {        System.out.println("six");    }    protected void seven() {        System.out.println("seven");    }    public void eight() {        System.out.println("eight");    }}