package enumeration.test.ex2;import java.util.Scanner;public class HttpStatusMain {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("HTTP CODE: ");        int httpCodeInput = scanner.nextInt();        HttpStatus status = HttpStatus.findByCode(httpCodeInput);        System.out.println(status.getCode() + " " + status.getMessage());        System.out.println("isSuccess = " + status.isSuccess());    }}